import logoIg from '../elements/imagenes/logo-ig.svg';
import '../estilos-todos.css';
import { useResponsiveTools } from '../elements/someFunctions.js';

const NumTelefono = () => {
    return (
        <div className='centrado yo-quiero-saber more-right-margin otra-prueba' height='20px'>
            <p className='social-networks-text centrado'>+99 999 9999 9999</p>
        </div>
    )
}
const Instagram = () => {
    return (
        <div className='centrado yo-quiero-saber more-right-margin'>
