            <div className='margins'>
                <br></br>
                {
                    windowHeight < 530 &&
                    <div>
                        <br></br>
                        <br></br>
                    </div>
                }
                <div className='centered' key='home'>
                    <img src={centralLogo} className="medium-image" alt="logo" />
                </div>
            </div >
            <SocialNetworks />
        </div>
    )
}
*/

export const Home = () => {
    return (
        <div className='background'>
            <TheHome />
        </div>
    )
}
