//             <Button onClick={() => props.addNumber}>+</Button>
//             <span className="input-group-text">{props.currentAmmount}</span>
//             <Button onClick={() => props.lessenNumber}>-</Button>
//         </div>
//     )
// }

export const useNewValues = () => {
    const {totalAmmount, getTotalAmmount} = useTotalAmmount();
    // const [totalAmmount, getTotalAmmount] = useState(3);
    const [localAmmount, getAmmount] = useState(0);
    const [currentKey, getKey] = useState(999);
    const increaseLocalAmmount = () => {
        getAmmount(localAmmount + 1);
        getTotalAmmount(totalAmmount + 1);
    }
    const fullDecrease = () => {
        getAmmount(localAmmount - 1);
        getTotalAmmount(totalAmmount - 1);
    }
    const decreaseLocalAmmount = () => {
        localAmmount >= 1 && fullDecrease();
    }
    const newKey = () => getKey(currentKey + 1);
    return { localAmmount, increaseLocalAmmount, decreaseLocalAmmount, newKey, totalAmmount }
}

const Artesania = (props) => {
    // const [currentAmmount, getAmmount] = useState(0);
    // const [currentKey, getKey] = useState(999);
    const { localAmmount, increaseLocalAmmount, decreaseLocalAmmount, newKey, totalAmmount } = useNewValues();
    return (
        <Card height='50px' width='50px'>
            <CardImg
                width='100%'
                alt="Img ilustrativa"
                src={props.imageSource}
            />
            <CardBody>
                <CardTitle tag="h5">
                    {props.title}
                </CardTitle>
                <CardSubtitle
                    className="mb-2 text-muted"
                    tag="h6"
                >
                    $10,00 por unidad
                </CardSubtitle>
                <CardText>
                    {props.description}
                    {totalAmmount}
                </CardText>
                {/* <GestorDeCantidades
                    addNumber={increaseAmmount}
                    lessenNumber={decreaseAmmount}
                    currentAmmount={currentAmmount}
                /> */}
                <div key={newKey} className="input-group mb-3">
                    <Button onClick={decreaseLocalAmmount}>-</Button>
                    <span className="input-group-text">{localAmmount}</span>
                    <Button onClick={increaseLocalAmmount}>+</Button>
                </div>
            </CardBody>
        </Card>
    )
}

// const useNewChildrenKey = () => {
//     const [childrenKey, setChildrenKey] = useState(100);
//     return { childrenKey, setChildrenKey };
// }

