import 'bootstrap/dist/css/bootstrap.css';
import '../styles.css';
import { Link as ReactLink } from 'react-router-dom';
import {
    UncontrolledDropdown, DropdownToggle, DropdownMenu, DropdownItem
} from 'reactstrap';
import { bikesSubcategories } from '../elements/subcategoriesList.js';

export const SecondCategory = (props) => {
    return (
        <DropdownItem>
            <ReactLink to={process.env.REACT_APP_FOR_PATH + '/' + props.link} alt='404'>{props.text}</ReactLink>
        </DropdownItem>
    )
}

export const FirstCategory = (props) => {
    return (
        <UncontrolledDropdown nav inNavbar>
            <DropdownToggle onClick={''} onMouseEnter={props.openCategory} onMouseLeave={props.openCategory}
                className='first-category-button navbar-custom'>{props.text}</DropdownToggle>
            <DropdownMenu>
                {
                    props.secondCategories.map((subcategory) => (
                        <SecondCategory
                            text={subcategory[0]}
                            link={subcategory[1]}
                        ></SecondCategory>
                    ))
                }
            </DropdownMenu>
        </UncontrolledDropdown>
    )
}
