import React from 'react';
import 'bootstrap/dist/css/bootstrap.css';
import { Col, Row } from 'reactstrap';
import '../estilos-todos.css';
import imagenCocinera from '../elements/imagenes/cocinera-chocolate.jpg';

const ComponenteAmpliado = () => {
    return (
        <div className='App-header margenes'>
            <div className='container-fluid' key='sobreNosotros'>
                <Row height='400px'>
                    <Col className=''>
                        <h4 className=''>
                            Somos una chocolatería artesanal y familiar con varios años en diversos campos de la cocina.
                            Nuestras artesanías son respaldadas por los aprendizajes adquiridos en instituciones reconocidas
                            y por materia prima de primera. Creemos que todo sale mejor cuando se hace con ganas,
                            y nos encanta lo que hacemos. ¡Te invitamos a compartir ese gusto con nosotros!
                        </h4>
                    </Col>
                    <Col className='' sm={1}>
                    </Col>
                    <Col className='centrado' sm={6}>
                        {/* <div className='imagen-mediana'> */}
                        <div className='centrado'>
                            <img className='imagen-mediana' src={imagenCocinera} alt='cocinera'></img>
                            {/* className='imagen-mediana' */}
                            {/* <img src={imagenCocinera} alt='cocinera' height='240px' width='637px'></img> */}
                            {/* <div className='anti-overflow'></div> */}
                        </div>
                    </Col>
                </Row>
            </div >
        </div >
    )
}

export const SobreNosotros = () => {
    return (
        <div className='fondo'>
            <ComponenteAmpliado />
        </div>
    )
}
