import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.css';
import '../App.css';
import {
    Card, CardImg, CardBody, CardTitle, CardSubtitle, CardText
} from 'reactstrap';
// import { CartContentContext } from './CartContent.js';
import { listaDeArtesanias } from '../elements/listaDeArtesanias.js';

const ContenidoDeCarrito = (props) => {
    const [currentKey, getKey] = useState(9999);
    const newKey = () => getKey(currentKey + 1);
    const algo = listaDeArtesanias[3][5];
    return (
        <div className='App-header'>
            return (
            <Card height='50px' width='50px'>
                <CardImg
                    width='100%'
                    alt="Img ilustrativa"
                    src={props.imageSource}
                />
                <CardBody>
                    <CardTitle tag="h5">
                        {props.title}
                    </CardTitle>
                    <CardSubtitle
                        className="mb-2 text-muted"
                        tag="h6"
                    >
                        $10,00 por unidad
                    </CardSubtitle>
                    <CardText>
                        {props.description}
                    </CardText>
                    {/* <GestorDeCantidades
                    addNumber={increaseAmount}
                    lessenNumber={decreaseAmount}
                    currentAmount={currentAmount}
                /> */}
                    <div key={newKey} className="input-group mb-3">
                        <span className="input-group-text">{localAmount}</span>
                    </div>
                </CardBody>
            </Card>
            )
            <h4>{algo}</h4>
        </div>
    )
}

export const Cart = () => {
    return (
        <div>
            <ContenidoDeCarrito
                refe={listaDeArtesanias[0][5]}
                title={listaDeArtesanias[0][0]}
                subtitle={listaDeArtesanias[0][2]}
                imageSource={listaDeArtesanias[0][1]}
                description={listaDeArtesanias[0][3]}
            />
        </div>
    )
}
