import 'bootstrap/dist/css/bootstrap.css';
import '../styles.css';
import { Link as ReactLink } from 'react-router-dom';
import {
    DropdownToggle, DropdownMenu, DropdownItem, Dropdown,
} from 'reactstrap';
import { useState } from 'react';
import { accesoriesSubcategories, apparelSubcategories, bikesSubcategories, componentsSubcategories } from '../elements/subcategoriesList.js';

export const CategoriesGroup = () => {
    const [bikesStatus, setBikesStatus] = useState(false);
    const [componentsStatus, setComponentsStatus] = useState(false);
    const [accesoriesStatus, setAccesoriesStatus] = useState(false);
    const [apparelsStatus, setApparelsStatus] = useState(false);

    const openBikes = () => setBikesStatus(bikesStatus ? false : true);
    const openComponents = () => setComponentsStatus(componentsStatus ? false : true);
    const openAccesories = () => setAccesoriesStatus(accesoriesStatus ? false : true);
    const openApparels = () => setApparelsStatus(apparelsStatus ? false : true);

    const Categories = (props) => {
        return (
            <Dropdown isOpen={props.categoryStatus} toggle={props.resetStatus}
                onMouseEnter={props.openCategory} onMouseLeave={props.openCategory} nav inNavbar>
                <DropdownToggle
                    className='first-category-button navbar-custom'>{props.text}</DropdownToggle>
                <DropdownMenu>
                    {
                        props.secondCategories.map((subcategory) => (
                            <DropdownItem>
                                <ReactLink
                                    to={process.env.REACT_APP_FOR_PATH + '/' + subcategory[1]} alt='404'>{subcategory[0]}
                                </ReactLink>
                            </DropdownItem>
                        ))
                    }
                </DropdownMenu>
            </Dropdown>
        )
    }

    return (
        <div>
            <Categories
                text={'Bicicletas'}
                openCategory={openBikes}
                categoryStatus={bikesStatus}
                secondCategories={bikesSubcategories}
                resetStatus={setBikesStatus(false)}
            ></Categories>
            <Categories
                text={'Componentes'}
                openCategory={openComponents}
                categoryStatus={componentsStatus}
                secondCategories={componentsSubcategories}
            ></Categories>
            <Categories
                text={'Accesorios'}
                openCategory={openAccesories}
                categoryStatus={accesoriesStatus}
                secondCategories={accesoriesSubcategories}
            ></Categories>
            <Categories
                text={'Aparejos'}
                openCategory={openApparels}
                categoryStatus={apparelsStatus}
                secondCategories={apparelSubcategories}
            ></Categories>
        </div>
    )
}
