                    </div>
                }
                <div className='centered' key='home'>
                    <img src={centralLogo} className="medium-image" alt="logo" />
                </div>
            </div >
            <SocialNetworks />
        </div>
    )
}
*/

export const Home = () => {
    return (
        <div className='background'>
            <HomeCarousel />
            <Products />
        </div>
    )
}
