        <div key='divPedido'>
            <br></br>
            {listaDeHileras.map((hilera) =>
                <div className='container-fluid-pedido' key={hilera[0][4] + hilera[0][4]}>
                    <Row className='dePrueba'>
                        {hilera.map((artesania) => (
                            <React.Fragment key={artesania[4]}>
                                <Col sm="4" className='dePrueba'>
                                    <Artesania
                                        title={artesania[0]}
                                        subtitle={artesania[2]}
                                        imageSource={artesania[1]}
                                        description={artesania[3]}
                                    />
                                </Col>
                            </React.Fragment>
                        ))}
                    </Row>
                    <br></br>
                </div>
            )}
            <br></br>
            <br></br>
        </div>
    )
}
