import React, { useContext, useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.css';
import '../estilos-todos.css';
import {
    Nav, NavItem, NavLink, Navbar, Collapse, NavbarText, UncontrolledAlert,
    Button, NavbarToggler, DropdownToggle, UncontrolledDropdown, DropdownMenu, NavbarBrand,
    DropdownItem
} from 'reactstrap';
import changuillo from '../elements/imagenes/changuito.svg';
import { TotalAmountContext } from './CartContent.js';

// const WindowTracker = () => {
//     const [windowWidth, setWindowWidth] = useState(window.innerWidth);
//     useEffect(() => {
//         const changeWindowWidth = () => {
//             setWindowWidth(window.innerWidth);
//         }
//         window.addEventListener('resize', changeWindowWidth);
//         return () => window.removeEventListener('resize', changeWindowWidth);
//     });
//     return (
//         <h4>{windowWidth}</h4>
//     )
// }


export const BarraSuperior = () => {
    const { carterTotalAmount } = useContext(TotalAmountContext);
    const showIt = localStorage.getItem('showCartHint');
    showIt || localStorage.setItem('showCartHint', 'true');
    const [showCartHint, setCartHint] = useState(showIt === 'true' ? true : false);
    const onDismiss = () => {
        setCartHint(false);
        localStorage.setItem('showCartHint', 'false');
    }
    const useResponsiveTools = () => {
        const [itemsAreVisible, setItemsVisibility] = useState(false);
        const hideItems = () => {
            setItemsVisibility(!itemsAreVisible);
        }
        const [windowWidth, setWindowWidth] = useState(window.innerWidth);
        useEffect(() => {
            const changeWindowWidth = () => {
                setWindowWidth(window.innerWidth);
            }
            window.addEventListener('resize', changeWindowWidth);
            return () => window.removeEventListener('resize', changeWindowWidth);
        });
        useEffect(() => {
            windowWidth < 768 && setItemsVisibility(false);
        }, [windowWidth]);
        return { hideItems };
    }
    const { hideItems, itemsAreVisible, windowWidth } = useResponsiveTools();
    return (
        <div>
            <Navbar color="warning" expand="md" fixed="top" light className="fw-bold">
                <NavbarToggler onClick={hideItems} />
                <Collapse navbar isOpen={itemsAreVisible}>
                    <Nav className="me-auto" navbar>
                        <NavItem>
                            <NavLink href="/">
                                Inicio
                            </NavLink>
                        </NavItem>
                        <NavItem>
                            <NavLink href='/pedido'>
                                ¡Hacé tu pedido acá!
                            </NavLink>
                        </NavItem>
                        <NavItem>
                            <NavLink href='/entregas'>
                                Dónde estamos
                            </NavLink>
                        </NavItem>
                        <NavItem>
                            <NavLink href='/sobre'>
                                Sobre nosotros
                            </NavLink>
                        </NavItem>
                        <NavItem>
                            <NavLink href='/contacto'>
                                Contacto
                            </NavLink>
                        </NavItem>
                        {/* <NavItem>
                            <NavbarText className='h4'>
                                <h4>{windowWidth}</h4>
                            </NavbarText>
                        </NavItem> */}
                        {
                            windowWidth < 768 &&
                            < NavbarText className='h4'>
                                <NavLink href='/carrito'>
                                    <img src={changuillo} alt='changuito' className='logo-mediano full-izq'></img>
                                    {carterTotalAmount}
                                </NavLink>
                            </NavbarText>
                        }
                    </Nav>
                    <Nav>
                        <NavItem key='MensajeDeAyuda'>
                            <UncontrolledAlert color="info" isOpen={showCartHint} toggle={onDismiss}>
                                Hacé clic en el changuito para ir al pedido
                            </UncontrolledAlert>
                        </NavItem>
                    </Nav>
                    {
                        windowWidth >= 768 &&
                        < NavbarText className='h4'>
                            <NavLink href='/carrito'>
                                <img src={changuillo} alt='changuito' className='logo-mediano'></img>
                                {carterTotalAmount}
                            </NavLink>
                        </NavbarText>
                    }
                </Collapse>
            </Navbar>
        </div >
    )
}
