import React, { useState, useMemo } from 'react';

export const useTotalAmount = () => {
    const [carterTotalAmmount, setTotalAmmount] = useState(0);
    const utilitiesSet = useMemo(
        () => ({ carterTotalAmmount, setTotalAmmount }),
        [carterTotalAmmount]
    );
    const TotalAmountContext = React.createContext(
        utilitiesSet
    );
    return TotalAmountContext;
}
