import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.css';
import '../styles.css';
// import centralLogo from '../elements/images/N-netflix.png';
// import { useResponsiveTools } from '../elements/someFunctions.js';
import { Carousel, CarouselCaption, CarouselItem, CarouselIndicators, CarouselControl } from 'reactstrap';
import { Products } from './Products.js';

const items = [
    {
        src: 'https://upload.wikimedia.org/wikipedia/commons/6/62/A_bicycle_wheel.jpg',
        altText: 'Slide 1',
        caption: 'Slide 1',
        key: 134535,
    },
    {
        src: 'https://upload.wikimedia.org/wikipedia/commons/6/62/A_bicycle_wheel.jpg',
        altText: 'Slide 2',
        caption: 'Slide 2',
        key: 26876876,
    },
    {
        src: 'https://upload.wikimedia.org/wikipedia/commons/6/62/A_bicycle_wheel.jpg',
        altText: 'Slide 3',
        caption: 'Slide 3',
        key: 3586586,
    },
];

const HomeCarousel = (args) => {
    const [activeIndex, setActiveIndex] = useState(0);
    const [animating, setAnimating] = useState(false);

    const next = () => {
        if (animating) return;
        const nextIndex = activeIndex === items.length - 1 ? 0 : activeIndex + 1;
        setActiveIndex(nextIndex);
    };

    const previous = () => {
        if (animating) return;
        const nextIndex = activeIndex === 0 ? items.length - 1 : activeIndex - 1;
        setActiveIndex(nextIndex);
    };

    const goToIndex = (newIndex) => {
        if (animating) return;
        setActiveIndex(newIndex);
    };

    const slides = items.map((item) => {
        return (
            <CarouselItem
                onExiting={() => setAnimating(true)}
                onExited={() => setAnimating(false)}
                key={item.key}
            >
                <img src={item.src} alt={item.altText} className='img-cls'/>
                <CarouselCaption
                    captionText={item.caption}
                    captionHeader={item.caption}
                />
            </CarouselItem>
        );
    });

    return (
        <Carousel
            activeIndex={activeIndex}
            next={next}
            previous={previous}
            {...args}
        >
            <CarouselIndicators
                items={items}
                activeIndex={activeIndex}
                onClickHandler={goToIndex}
            />
            {slides}
            <CarouselControl
                direction="prev"
                directionText="Previous"
                onClickHandler={previous}
            />
            <CarouselControl
                direction="next"
                directionText="Next"
                onClickHandler={next}
            />
        </Carousel>
    );
}

