import React, { useState, useMemo } from 'react';

const useCartAmount = () => {
    const [carterTotalAmmount, setTotalAmmount] = useState(0);
    const utilitiesSet = useMemo(
        () => ({ carterTotalAmmount, setTotalAmmount }),
        [carterTotalAmmount]
    );
    return utilitiesSet;
}

export const TotalAmountContext = React.createContext({
    useCartAmount
});


