import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.css';
import '../App.css';
import {
    Card, CardImg, CardBody, CardTitle, CardSubtitle, CardText, Button, Col, Row
} from 'reactstrap';
// import { CartContentContext } from './CartContent.js';
import { listaDeArtesanias } from '../elements/listaDeArtesanias.js';

const ContenidoDeCarrito = (props) => {
    const [currentKey, getKey] = useState(9999);
    const newKey = () => getKey(currentKey + 1);
    // const algo = listaDeArtesanias[3][5];
    return (
        <div className='App-header'>
            <Card height='50px' width='50px'>
                <CardImg
                    width='80%'
                    alt="Img ilustrativa"
                    src={props.imageSource}
                />
                <CardBody>
                    <CardTitle tag="h5">
                        {props.title}
                    </CardTitle>
                    <CardSubtitle
                        className="mb-2 text-muted"
                        tag="h6"
                    >
                        $10,00 por unidad
                    </CardSubtitle>
                    <CardText className="text-dark">
                        {props.description}
                    </CardText>
                    {/* <GestorDeCantidades
                    addNumber={increaseAmount}
                    lessenNumber={decreaseAmount}
                    currentAmount={currentAmount}
                /> */}
                    <div key={newKey} className="input-group mb-3">
                        <span className="input-group-text">Acá iría la cantidad</span>
                    </div>
                </CardBody>
            </Card>
            {/* <h4>{algo}</h4> */}
        </div>
    )
}

const listaDeHileras = [['soloUno']];

export const Cart = () => {
    return (
        <div key='divPedido'>
            <br></br>
            <br></br>
            <br></br>
            <div className="input-group mb-3 alineado-horizontal">
                <Button className='disabled' onClick={() => { }}>Anterior</Button>
                <span className="input-group-text">Página 1 de 1</span>
                <Button className='disabled' onClick={() => { }}>Siguiente</Button>
            </div>
            {listaDeHileras.map((hilera) =>
                <div className='container-fluid' key={hilera[0][4] + hilera[0][4]}>
                    <Row>
                        {hilera.map((artesania) => (
                            <React.Fragment key={artesania[4]}>
                                <Col sm="4">
                                    <ContenidoDeCarrito
                                        refe={listaDeArtesanias[0][5]}
                                        title={listaDeArtesanias[0][0]}
                                        subtitle={listaDeArtesanias[0][2]}
                                        imageSource={listaDeArtesanias[0][1]}
                                        description={listaDeArtesanias[0][3]}
                                    />
                                </Col>
                            </React.Fragment>
                        ))}
                    </Row>
                    <br></br>
                </div>
            )}
        </div>
    )
}
