//             <Row>
//                 <Col sm="5">
//                     <div className="orientacion-derecha">
//                         <Button href="/pedido" onClick={() => { }}>
//                             Volver al cat√°logo
//                         </Button>
//                     </div>
//                 </Col>
//                 <Col sm="2">
//                     <div className="input-group mb-3 alineado-horizontal">
//                         <span className="input-group-text"
