import 'bootstrap/dist/css/bootstrap.css';
import React, { Component, useState, useMemo, createContext } from 'react';
import {
    BrowserRouter as Router,
    Routes,
    Route,
    Outlet
} from "react-router-dom";
import './estilos-todos.css';
import { BarraSuperior } from './components/BarraSuperior.js';
import { BarraInferior } from './components/BarraInferior.js';
import './App.css';
import { Cuerpo } from './components/Cuerpo.js';
import { SobreNosotros } from './components/SobreNosotros.js';
import { Entregas } from './components/Entregas.js';
import { Inicio } from './components/Inicio.js';
import { NuestrosProductos } from './components/NuestrosProductos.js';
import { Pedido } from './components/Pedido.js';
import { PaginaNoEncontrada } from './components/PaginaNoEncontrada.js';
// import { useTotalAmmount } from './components/Pedido.js';


// useTotalAmmount().totalAmmount
// const useSmthg = () => {

//     const ammountElements = document.getElementsByClassName('input-group-text');
//     const currentAmmount = useTotalAmmount().totalAmmount;
//     const [updateOfAmmount, getUpdateOfAmmount] = useState(useTotalAmmount().totalAmmount);
//     useEffect(() => {
//         getUpdateOfAmmount(currentAmmount);
//     }, [ammountElements]);
//     return updateOfAmmount;
// }

// const useTotalAmmount = () => {
//     const [carterTotalAmmount, getTotalAmmount] = useState(4);
//     const increaseTotal = () => getTotalAmmount(carterTotalAmmount + 1);
//     //ESTA FUNCIÓN NO ES QUE NO SE ENTIENDE CON EL HOOK SINO QUE NI SIQUIERA ESTÁ SIENDO LLAMADA
//     const decreaseTotal = () => carterTotalAmmount > 0 && getTotalAmmount(carterTotalAmmount - 1);
//     return { carterTotalAmmount, increaseTotal, decreaseTotal }
// }

export const TotalAmmountContext = React.createContext({
    carterTotalAmmount: '',
    setTotalAmmount: () => {},
});

const General = (props) => {
    const [carterTotalAmmount, setTotalAmmount] = useState(0);
    const utilitiesSet = useMemo(
        () => ({ carterTotalAmmount, setTotalAmmount }),
        [carterTotalAmmount]
    );
    // const increaseTotal = () => getTotalAmmount(carterTotalAmmount + 1);
    // const decreaseTotal = () => carterTotalAmmount > 0 && getTotalAmmount(carterTotalAmmount - 1);

    // const { carterTotalAmmount, increaseTotal, decreaseTotal } = useTotalAmmount();
    // const utilitiesSet = { carterTotalAmmount, increaseTotal, decreaseTotal };
    // const carterTotalAmmount = '5';
    return (
        <TotalAmmountContext.Provider
            value={utilitiesSet}>
            <div>
                {document.title = 'Rafaela'}
                <BarraSuperior
                />
                <Cuerpo
                    content={props.contenido}
                ></Cuerpo>
                <BarraInferior />
            </div>
        </TotalAmmountContext.Provider>
    );
}

class Principal extends Component {
    state = {
        data: null
    };

    componentDidMount() {
        this.callBackendAPI()
            .then(res => this.setState({ data: res.express }))
            .catch(err => console.log(err));
    }

    callBackendAPI = async () => {
        const response = await fetch('/express_backend');
        const body = await response.json();

        if (response.status !== 200) {
            throw Error(body.message)
        }
        return body;
    };

    render() {
        return (
            <Router>
                <div>
                    <Routes>
                        <Route path='/' element={<General contenido={<Outlet />} />}>
                            <Route index element={<Inicio />}></Route>
                            <Route path={'/pedido'} element={<Pedido />}></Route>
                            <Route path={'/nuestros-productos'} element={<NuestrosProductos />}></Route>
                            <Route path={'/entregas'} element={<Entregas />}></Route>
                            <Route path={'/sobre'} element={<SobreNosotros />}></Route>
                            <Route path={'*'} element={<PaginaNoEncontrada />}></Route>
                        </Route>
                    </Routes>
                    {console.log(this.state.data)}
                </div>
            </Router >
        )
    }
}

export default Principal;
