import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.css';
import '../App.css';
import {
    Card, CardImg, CardBody, CardTitle, CardSubtitle, CardText, Button, Col, Row
} from 'reactstrap';
import { listaDeArtesanias } from '../elements/listaDeArtesanias.js';

const hileraUnoArtesanias = [listaDeArtesanias[0], listaDeArtesanias[1], listaDeArtesanias[2]];
const hileraDosArtesanias = [listaDeArtesanias[3], listaDeArtesanias[4], listaDeArtesanias[5]];
const hileraTresArtesanias = [listaDeArtesanias[6], listaDeArtesanias[7], listaDeArtesanias[8]];

const listaDeHileras = [hileraUnoArtesanias, hileraDosArtesanias, hileraTresArtesanias];

// const GestorDeCantidades = (props) => {
//     const [currentKey, getKey] = useState(999);
//     const newKey = () => getKey(currentKey + 1);
//     return (
//         <div key={newKey} className="input-group mb-3">
//             <Button onClick={() => props.addNumber}>+</Button>
//             <span className="input-group-text">{props.currentAmmount}</span>
//             <Button onClick={() => props.lessenNumber}>-</Button>
//         </div>
//     )
// }

export const useNewValues = () => {
    // const { totalAmmount, getTotalAmmount } = useTotalAmmount();
    // const [totalAmmount, getTotalAmmount] = useState(3);
    const [localAmmount, getAmmount] = useState(0);
    const [currentKey, getKey] = useState(999);
    const increaseLocalAmmount = () => {
        getAmmount(localAmmount + 1);
    }
    const fullDecrease = () => {
        getAmmount(localAmmount - 1);
    }
    const decreaseLocalAmmount = () => {
        localAmmount >= 1 && fullDecrease();
    }
    const newKey = () => getKey(currentKey + 1);
    return { localAmmount, increaseLocalAmmount, decreaseLocalAmmount, newKey }
}

const Artesania = (props) => {
    // const [currentAmmount, getAmmount] = useState(0);
    // const [currentKey, getKey] = useState(999);
    const { localAmmount, increaseLocalAmmount, decreaseLocalAmmount, newKey } = useNewValues();
    const decreaseFunction = () => {
        decreaseLocalAmmount();
        props.decreaseTotalAmmount();
    }
    const increaseFunction = () => {
        increaseLocalAmmount();
        props.increaseTotalAmmount();
    }
    return (
        <Card height='50px' width='50px'>
            <CardImg
                width='100%'
                alt="Img ilustrativa"
                src={props.imageSource}
            />
            <CardBody>
                <CardTitle tag="h5">
                    {props.title}
                </CardTitle>
                <CardSubtitle
                    className="mb-2 text-muted"
                    tag="h6"
                >
                    $10,00 por unidad
                </CardSubtitle>
                <CardText>
                    {props.description}
                </CardText>
                {/* <GestorDeCantidades
                    addNumber={increaseAmmount}
                    lessenNumber={decreaseAmmount}
                    currentAmmount={currentAmmount}
                /> */}
                <div key={newKey} className="input-group mb-3">
                    <Button onClick={decreaseFunction}>-</Button>
                    <span className="input-group-text">{localAmmount}</span>
                    <Button onClick={increaseFunction}>+</Button>
                </div>
            </CardBody>
        </Card>
    )
}

// const useNewChildrenKey = () => {
//     const [childrenKey, setChildrenKey] = useState(100);
//     return { childrenKey, setChildrenKey };
// }

export const useTotalAmmount = () => {
    // const listOfAmmounts = document.getElementsByClassName('input-group-text');
    // console.log(listOfAmmounts[0].value);
    const [totalAmmount, getTotalAmmount] = useState(3);
    return { totalAmmount, getTotalAmmount }
}

export const Pedido = () => {
    const { totalAmmount, getTotalAmmount } = useTotalAmmount();
    // const { childrenKey, setChildrenKey } = useNewChildrenKey();
    // const [childrenKey, setChildrenKey] = useState(100);
    const increaseTotal = () => getTotalAmmount(totalAmmount + 1);
    const decreaseTotal = () => totalAmmount <= 0 && getTotalAmmount(totalAmmount - 1);

    //     {/* {setChildrenKey(childrenKey + 1)} */}
    // {/* key={childrenKey} */}
    // const totalAmmount = useNewValues().totalAmmount;
    return (
        <div key='divPedido'>
            <br></br>
            <br></br>
            <br></br>
            <div className="input-group mb-3 alineado-horizontal">
                <Button className='disabled' onClick={() => { }}>Anterior{totalAmmount}</Button>
                <span className="input-group-text">Página 1 de 1</span>
                <Button className='disabled' onClick={() => { }}>Siguiente</Button>
            </div>
            {listaDeHileras.map((hilera) =>
                <div className='container-fluid' key={hilera[0][4] + hilera[0][4]}>
                    <Row>
                        {hilera.map((artesania) => (
                            <React.Fragment key={artesania[4]}>
                                <Col sm="4" className='dePrueba'>
                                    <Artesania
                                        title={artesania[0]}
                                        subtitle={artesania[2]}
                                        imageSource={artesania[1]}
                                        description={artesania[3]}
                                        increaseTotalAmmount={increaseTotal}
                                        decreaseTotalAmmount={decreaseTotal}
                                    />
                                </Col>
                            </React.Fragment>
                        ))}
                    </Row>
                    <br></br>
                </div>
            )}
        </div>
    )
}
