import 'bootstrap/dist/css/bootstrap.css';
import '../styles.css';
import { Link as ReactLink } from 'react-router-dom';
import {
    UncontrolledDropdown, DropdownToggle, DropdownMenu, DropdownItem, Dropdown, Collapse
} from 'reactstrap';
import { useState } from 'react';

export const Categories = (props) => {

    //const [bikesStatus, setBikesStatus] = useState(false);
    /*
        const openBikes = () => {
            console.log(`Antes del evento: ${bikesStatus}`);
            bikesStatus === true ? setBikesStatus(false) : setBikesStatus(true);
            console.log(`Dopo del evento: ${bikesStatus}`);
        }
        */
    return (
        <Dropdown isOpen={props.categoryStatus} toggle={props.openCategory} 
        onMouseEnter={props.openCategory} 
        onMouseLeave={props.openCategory} nav inNavbar>
            <DropdownToggle     
                className='first-category-button navbar-custom'>{props.text}</DropdownToggle>
            <DropdownMenu>
                {
                    props.secondCategories.map((subcategory) => (
                        <DropdownItem>
                            <ReactLink
                                to={process.env.REACT_APP_FOR_PATH + '/' + subcategory[1]} alt='404'>{subcategory[0]}
                            </ReactLink>
                        </DropdownItem>
                    ))
                }
            </DropdownMenu>
        </Dropdown>
    )
}
