import React from 'react';
import 'bootstrap/dist/css/bootstrap.css';
import '../App.css';
import {
    CardGroup, Card, CardImg, CardBody, CardTitle, CardSubtitle, CardText, Button
} from 'reactstrap';

//<div className='App-header'>
//<h1>Caíste, no me podés pedir nada. Todavía no funciono =)</h1>
//</div>
/*  
barks con almendras y cada cual con los tres choco
con galletitas oreo
con sal marina
con cereales
con menta
tulipas de chocolate
las tabletas
semiesferas de chocolate con ddl
milhojas de ddl

tableta con cereales
tableta con caramelo
tableta con menta
trufa con frutos del bosque
minihuevitos blancos
minihuevitos semiamargo
minihuevitos leche
 */
/* 
<div key='contenedor-componentes'>
{artesanias.map((artesania) => (
    <div key={artesania[0]} className='App-header'>
        <p>{artesania[0]}</p>
        <p>{artesania[1]}</p>
    </div>
))}
</div>
 */
const listaDeArtesanias = [
    [
        'Bombón con ganache',
        'https://cdn.shopify.com/s/files/1/0247/3731/6909/products/Chocolateartesanal_almendra05_cuadrado_1024x1024.jpg?v=1633796874',
        'Pasen y vean qué lindas tolderías',
        'Descripción de la toldería',
        '0'
    ],
    [
        'Tableta con cereales',
        'https://cdn.shopify.com/s/files/1/0247/3731/6909/products/Chocolateartesanal_almendra05_cuadrado_1024x1024.jpg?v=1633796874',
        'Pasen y vean qué lindas tolderías',
        'Descripción de la toldería',
        '1'
    ],
    [
        'Barks con oreo',
        'https://cdn.shopify.com/s/files/1/0247/3731/6909/products/Chocolateartesanal_almendra05_cuadrado_1024x1024.jpg?v=1633796874',
        'Pasen y vean qué lindas tolderías',
        'Descripción de la toldería',
        '2'
    ]
]

const Artesania = (props) => {
    return (
        <Card key={props.key}>
            <CardImg
                alt="Img ilustrativa"
                src={props.imageSource}
                height='400px'
            />
            <CardBody>
                <CardTitle tag="h5">
                    {props.title}
                </CardTitle>
                <CardSubtitle
                    className="mb-2 text-muted"
                    tag="h6"
                >
                    {/* {props.subtitle} */}
                </CardSubtitle>
                <CardText>
                    {props.description}
                </CardText>
                <Button>
                    Más detalles
                </Button>
            </CardBody>
        </Card>
    )
}

export const Pedido = () => {
    return (
        <div>
            <CardGroup>
                {listaDeArtesanias.map((artesania) => (
                    <Artesania
                        title={artesania[0]}
                        subtitle={artesania[2]}
                        key={artesania[4]}
                        imageSource={artesania[1]}
                        description={artesania[3]}
                    />
                ))}
            </CardGroup>
        </div>
    )
}
