const listaDeHileras = [hileraUnoArtesanias, hileraDosArtesanias, hileraTresArtesanias];

const Artesania = (props) => {
    return (
        <Card>
            <CardImg
                width='100%'
                alt="Img ilustrativa"
                src={props.imageSource}
